
var isstream = require('isstream')


function body (req, options) {
  if (isstream(options.body)) {
    options.body.pipe(req)
  }
  else if (Array.isArray(options.body)) {
    options.body.forEach(function (part) {
      req.write(part)
    })
  }
  else {
    req.write(options.body)
  }
}

module.exports = body
